import React, { useState , useContext} from "react";

import { Box, Typography, Button, styled, Container } from "@mui/material";
// import {shoppingCart} from '@mui/icons-material';
import { Con } from '../context/ContextProvider';


//components
import LoginDialog from '../login/LoginDialog';
import Profile from './Profile';


const Wrapper = styled(Box)(({ theme }) => ({
  margin: "0 3% 0 auto",
  display: "flex",
  "& > *": {
    marginRight: "40px !important",
    textDecoration: "none",
    color: "#FFFFFF",
    fontSize: 12,
    alignItems: "center",
    [theme.breakpoints.down("sm")]: {
      color: "#2874f0",
      alignItems: "center",
      display: "flex",
      flexDirection: "column",
      marginTop: 10,
    },
  },
  [theme.breakpoints.down("sm")]: {
    display: "block",
  },
}));

const LoginButton = styled(Button)(({ theme }) => ({
  color: "#2874f0",
  background: "#FFFFFF",
  textTransform: "none",
  fontWeight: 600,
  borderRadius: 2,
  padding: "5px 40px",
  height: 32,
  boxShadow: "none",
  [theme.breakpoints.down("sm")]: {
    background: "#2874f0",
    color: "#FFFFFF",
  },
}));

const CustomButtons = () => {
  const [open, setOpen] = useState(false);
  const [acccount, setAccount] = useContext(LoginContext);
  const openDialog = () => {
    setOpen(true);
  }
  return (
    <Wrapper>
      {
        acccount ? <Profile>account = {acccount} setAccount = {setAccount}</Profile> : <LoginButton variant="contained" onClick={()=> openDialog()}>Login</LoginButton>
      }
      <LoginButton variant="contained" onClick={()=> openDialog()}>Login</LoginButton>
      <Typography style={{ marginTop: 3, width: 135 }}>
        Become a Seller
      </Typography>
      <Typography style={{ marginTop: 3 }}>More</Typography>

      <Container>
        <shoppingCart />
        <Typography style={{ marginLeft: 10 }}>Cart</Typography>
      </Container>
      <LoginDialog open = {open} setOpen = {setOpen}/>

    </Wrapper>
  );
};

export default CustomButtons;
